function r(a){return new Promise((o,e)=>{setTimeout(()=>{console.log(`📋 Entrando llamada de pedido #${a}.`),Math.random()>.1?o(a):e(`❌ Error al tomar el pedido #${a}.`)},Math.random()*1e3+500)})}function t(a){return new Promise((o,e)=>{setTimeout(()=>{console.log(`🧑‍🍳 Revisando ingredientes del pedido #${a}.`),Math.random()>.05?o(a):e(`⚠️ Error al preparar ingredientes del pedido #${a}.`)},Math.random()*1e3+1e3)})}function n(a){return new Promise((o,e)=>{setTimeout(()=>{console.log(`🍕 Pizza del pedido #${a} en el horno.`),Math.random()>.05?o(a):e(`⚠️ Error al cocinar la pizza del pedido #${a}.`)},Math.random()*1e3+2e3)})}function i(a){return new Promise((o,e)=>{setTimeout(()=>{console.log(`📦 Empaquetando pedido #${a}.`),Math.random()>.01?o(a):e(`⚠️ Error al empaquetar el pedido #${a}.`)},Math.random()*1e3+500)})}function s(a){return new Promise((o,e)=>{setTimeout(()=>{console.log(`🏍️ Llevando pedido #${a}.`),Math.random()>.08?o(`✅ Cliente del pedido #${a} satisfecho.`):e(`⚠️ Error al entregar el pedido #${a}.`)},Math.random()*1e3+3e3)})}async function l(a){try{await r(a),await t(a),await n(a),await i(a);const o=await s(a);return console.log(o),o}catch(o){throw console.error(o),o}}async function c(){const o=[1,2,3].map(e=>l(e));try{await Promise.all(o),console.log("🚀 Procesamiento de pedidos finalizado.")}catch{console.log("😡 Esto no va bien")}}c();
